  const [show, setShow] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setShow(true), 1800);
    return () => clearTimeout(timer);
  }, []);

  return (
    show ? 
    <Typewriter
      options={{
        strings: [
          `${props.name}`,
          "a Frontend Web Developer",
          "a MERN Stack Enthusiast"
        ],
        autoStart: true,
        loop: true,
        deleteSpeed: 35
      }}
    />
    : <></>
  );
}

export default Type;
